import React from 'react';
import {Marker,useJsApiLoader, GoogleMap} from '@react-google-maps/api'
import {Flex,SkeletonText} from '@chakra-ui/react'
const center = { lat: 48.8584, lng: 2.2945 }
function Map(){
  const { isLoaded } = useJsApiLoader({
    googleMapsApiKey: 'AIzaSyDkJDTVx0PEYQMhrcSCtu8lO21clQxN6so',
    libraries: ['places'],
  })
  const [map, setMap] = React.useState(/** @type google.maps.Map */ (null))
  if (!isLoaded) {
    return <div></div>
  }
  return (
    
    <Flex
    position='relative'
    flexDirection='column'
    alignItems='center'
    height='300px'
    w='100vw'
    >
        <GoogleMap
          center={center}
          zoom={3}
          mapContainerStyle={{ width: '90%', height: '100%' }}
          options={{
            zoomControl: false,
            streetViewControl: false,
            mapTypeControl: false,
            fullscreenControl: false,
          }}
          onLoad={map => setMap(map)}
        >
          <div position={center} > "Abc"</div>
        </GoogleMap>
        </Flex>
  )
}

export default Map;
