import React from 'react';
import axios from 'axios';
import Map from './Map';
import Tabel from './Tabel';
export default function Main({name})
{
    const api = axios.create({
       baseURL: `https://627e1ec9b75a25d3f3b21451.mockapi.io`
   })
    // --- Generare vector cu id-urile spot-urilor favorite :
    const [favourites,setFavourites] = React.useState([])
    React.useEffect(()=>{
    const fetch = async () =>{
        try{
            const response = await api.get('/favourites')
          setFavourites(response.data)
      } catch(err){
          if(err.response){
              console.log(err.response.data)
              console.log(err.response.status)
              console.log(err.response.header)
          }
          else {
              console.log(`Error: ${err.message}`)
          }
      }
      }
      fetch();
        },[])
    const userFavourites = favourites.filter( fav => fav.userName == name)
  // ---------------------------------------------------------
   const [data,setData] = React.useState([])
    React.useEffect(()=>{
        const fetch = async () =>{
            try{
                const response = await api.get('/spot')
                setData(response.data)
            } catch(err){
                if(err.response){
                    console.log(err.response.data)
                    console.log(err.response.status)
                    console.log(err.response.header)
                }
                else {
                    console.log(`Error: ${err.message}`)
                }
            }
        }
        fetch();
    },[])
    function search(rows){
        return rows.filter(row => row.country.toLowerCase().indexOf(q) > -1  
        || row.country.toUpperCase().indexOf(q) > -1 
        ||  row.country.indexOf(q) > -1)
    }
    const [q,setQ] = React.useState("");
    return(
        <div className="Main">
            <Map data={data} name={name} userFavourites={userFavourites}/>
            <div className="search-filt">
            <h2>Locations</h2>
            <input type="text" value={q} onChange={ (e) => setQ(e.target.value)} />
            </div>
            <Tabel data={search(data)}/>
        </div>
    )
}
