import React from 'react';
import {OverlayView} from '@react-google-maps/api'
import Card from './Card';
import axios from 'axios';
export default function CustomMarker({features,name})
{
    const api = axios.create({
        baseURL: `https://627e1ec9b75a25d3f3b21451.mockapi.io`});
    
    const [users,setUsers] = React.useState([])
    React.useEffect(()=>{
    const fetchUsers = async () =>{
        try{
            const response = await api.get('/user')
            setUsers(response.data)
        } catch(err){
            if(err.response){
                console.log(err.response.data)
                console.log(err.response.status)
                console.log(err.response.header)
            }
            else {
                console.log(`Error: ${err.message}`)
            }
        }
        }
        fetchUsers();
    },[])
    const currUser = users.filter(user => user.name==name)

    const [favourites,setFavourites] = React.useState([])
    React.useEffect(()=>{
    const fetch = async () =>{
        try{
            const response = await api.get('/favourites')
            setFavourites(response.data)
        } catch(err){
            if(err.response){
                console.log(err.response.data)
                console.log(err.response.status)
                console.log(err.response.header)
            }
            else {
                console.log(`Error: ${err.message}`)
            }
        }
        }
        fetch();
    },[])

    const isMarkerFav = favourites.filter(fav => fav.userId == currUser[0].userId && fav.id == features.id)
    var isFav = false
    if(isMarkerFav.length > 0) isFav = true; 
    const center = { lat: features.lat, lng: features.long }
    const infoCenter = { lat: features.lat+10, lng: features.long+10 }
    const [info,setInfo] = React.useState(false);
    function changeInfo()
    {
        setInfo(prevInfo => !prevInfo)
    }
    if(isFav==false)
    return(
        <OverlayView
            position={center}
            mapPaneName={OverlayView.OVERLAY_MOUSE_TARGET} >
            <div>
            <div className="marker">
                <img src="https://cdn-icons-png.flaticon.com/128/684/684908.png" alt="" className="header-img" onClick={changeInfo}/>
            </div>
            {info && <div  onClick={changeInfo}>
                    <Card name={name} features={features}/>
                </div> }
            </div>
        </OverlayView>
        )
    else
    return(
        <OverlayView
            position={center}
            mapPaneName={OverlayView.OVERLAY_MOUSE_TARGET} >
            <div>
            <div className="marker">
                <img src="https://cdn-icons-png.flaticon.com/128/1502/1502944.png" alt="" className="header-img" onClick={changeInfo}/>
            </div>
            {info && <div  onClick={changeInfo}>
                    <Card name={name} features={features}/>
                </div> }
            </div>
        </OverlayView>
        )
}
