import React, {useState} from 'react';
import DashBoard from './components/DashBoard';
import LoginForm from './components/LoginForm';
import axios from 'axios';
function App() {
  const [user, setUser] = useState({name:"",password:""});
  const [error, setError] = useState("");
  const [favourites, setFavourites] = useState([]);
  const [favModified, setFavModified] = useState(false)
  const Login = details => {
    setUser({name:details.name,password:details.password});
  }

  const setFav = fav => {
    setFavourites(fav)
  }


  const Logout = () => {
    setUser({name:"",password:""})
  }
  function change()
  {
    setFavModified(prevState => !prevState)
  }
  React.useEffect(()=>{
    const api = axios.create({
        baseURL: `https://627e1ec9b75a25d3f3b21451.mockapi.io`
    })
    const fetch = async () =>{
        try{
            const response = await api.get('/favourites')
            setFav(response.data)
        } catch(err){
            if(err.response){
                console.log(err.response.data)
                console.log(err.response.status)
                console.log(err.response.header)
            }
            else {
                console.log(`Error: ${err.message}`)
            }
        }
    }
    fetch();
},[favModified])
  return (
    <div className="App">
      {
      user.name != "" ? <DashBoard change={change} favourites={favourites} name={user.name} Logout={Logout} /> 
                        : <LoginForm setFav={setFav} Login={Login} error={error} />
      }
    </div>
  );
}

export default App;
