import React from 'react';
import {OverlayView,Marker,useJsApiLoader, GoogleMap} from '@react-google-maps/api'
import {Flex,SkeletonText} from '@chakra-ui/react'
import CustomMarker from './CustomMarker';
import AddSpot from './AddSpot';
import axios from 'axios';
const center = { lat: 48.8584, lng: 2.2945 }
function Map({data,name}){
  // --- Generare vector cu id-urile spot-urilor favorite :
  /*const api = axios.create({
    baseURL: `https://627e1ec9b75a25d3f3b21451.mockapi.io`});
  
  const [favourites,setFavourites] = React.useState([])
  React.useEffect(()=>{
  const fetch = async () =>{
      try{
          const response = await api.get('/favourites')
          setFavourites(response.data)
      } catch(err){
          if(err.response){
              console.log(err.response.data)
              console.log(err.response.status)
              console.log(err.response.header)
          }
          else {
              console.log(`Error: ${err.message}`)
          }
      }
      }
      fetch();
    },[])
  const userFavourites = favourites.filter( fav => fav.userName == name)
  console.log(userFavourites)*/
  // ---------------------------------------------------------
  const userFavourites = []
  const f = false;
  const { isLoaded } = useJsApiLoader({
    googleMapsApiKey: 'AIzaSyDkJDTVx0PEYQMhrcSCtu8lO21clQxN6so',
    libraries: ['places'],
  })
  const [map, setMap] = React.useState(/** @type google.maps.Map */ (null))
  if (!isLoaded) {
    return <div></div>
  }
  return (
    <div className='all-map'>
      {f && <AddSpot />}
    <div className='map-div'>
    <Flex
    position='relative'
    flexDirection='column'
    alignItems='center'
    height='380px'
    w='100vw'
    >
        <GoogleMap
          center={center}
          zoom={3}
          mapContainerStyle={{ width: '90%', height: '100%' }}
          options={{
            zoomControl: false,
            streetViewControl: false,
            mapTypeControl: false,
            fullscreenControl: false,
          }}
          onLoad={map => setMap(map)}
        >
        {data.map(features => <CustomMarker favourites={userFavourites} name={name} key={features.id} features={features}/>)}  
        </GoogleMap>
    </Flex>
    </div>
    </div>
  )
}

export default Map;
