import React, {useState} from 'react';
import DashBoard from './components/DashBoard';
import LoginForm from './components/LoginForm';
import axios from 'axios';
function App() {
  const [user, setUser] = useState({name:"",password:""});
  const [error, setError] = useState("");
  const [userFavourites,setUserFavourites] = React.useState([])
  const Login = details => {
    setUser({name:details.name,password:details.password})

    function makeGetRequest(path) {
      axios.get(path).then(
          (response) => {
              setUserFavourites(response.data.filter(data => data.userName == details.name))
          },
          (error) => {
              console.log(error);
          }
      );
      }
    makeGetRequest('https://627e1ec9b75a25d3f3b21451.mockapi.io/favourites');
  }

  const Logout = () => {
    setUser({name:"",password:""})
  }

  return (
    <div className="App">
      {
      user.name != "" ? <DashBoard userFavourites={userFavourites} name={user.name} Logout={Logout} /> 
                        : <LoginForm Login={Login} error={error} />
      }
    </div>
  );
}

export default App;
