import React, {useState} from 'react';
import DashBoard from './components/DashBoard';
import LoginForm from './components/LoginForm';
import axios from 'axios';
function App() {
  const [user, setUser] = useState({name:"",password:""});
  const [error, setError] = useState("");
  const [userFavourites,setFavs] = useState([])
  const Login = details => {
    setUser({name:details.name,password:details.password})
    const api = axios.create({
      baseURL: `https://627e1ec9b75a25d3f3b21451.mockapi.io`
  })
   // --- Generare vector cu id-urile spot-urilor favorite :
   const [favourites,setFavourites] = React.useState([])
   React.useEffect(()=>{
   const fetch = async () =>{
       try{
           const response = await api.get('/favourites')
         setFavourites(response.data)
     } catch(err){
         if(err.response){
             console.log(err.response.data)
             console.log(err.response.status)
             console.log(err.response.header)
         }
         else {
             console.log(`Error: ${err.message}`)
         }
     }
     }
     fetch();
       },[])
   const tempFavs = favourites.filter( fav => fav.userName == details.name)
   console.log(tempFavs)
 // ---------------------------------------------------------
  }

  const Logout = () => {
    setUser({name:"",password:""})
  }

  return (
    <div className="App">
      {
      user.name != "" ? <DashBoard userFavourites={userFavourites} name={user.name} Logout={Logout} /> 
                        : <LoginForm Login={Login} error={error} />
      }
    </div>
  );
}

export default App;
